<!DOCTYPE html><html lang="ru"><head><meta charset=utf-8><title><?=$title?></title><?=$meta_keywords?><?=$meta_description?><?=Loadlib::view_css($css)?><?=Loadlib::view_js($js)?></head><body>	<div id="opacity_background">	</div>	<div id="block_form">	</div>	<div id="abstract">	</div>	<div id="container">		<div class="bg_texture">			<div class="name_form_login"><?=$title_form?></div>			<form name="login" id="form_login" action="<?=url::base()?>login" method="POST">				<div class="input_text">					<div class="shadow_input_text">						<input type="text" name="username" class="input_login" id="input_username" error="Введите имя пользователя" />					</div>				</div>				<br/>				<div class="input_text" >					<div class="shadow_input_text">						<input type="password" name="password" class="input_login" id="input_password" error="Введите пароль" />					</div>				</div>				<br/>				<div class="input_checkbox">					<input type="checkbox" name="remember_me" />					Запомнить меня				</div>				<div id="register">					<a href="<?=url::base()?>register">Зарегистрироваться?</a>				</div>				<input type="submit" value="Войти уже пора" id="submit_button"/>			</form>		</div>	</div>	<div id="notify_form_login"><?=$notify?></div></body></html>